insert into CREATURE_SUBTYPES (ID,TYPE,DESCRIPTION) values (51,'Inevitable','Inevitables are constructs whose sole aim is to enforce the natural laws of the universe.');
insert into type_subtype (type_id, SUB_ID) values (4,51);
